sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue == '') {
        return;
    }

    var options = {
        "hr": [{
            option: "hr1",
            optValue: "Human Resources 1"
        }, {
            option: "hr2",
            optValue: "Human Resources 2"
        }],
        "facilities": [{
            option: "facilities1",
            optValue: "Facilities 1"
        }, {
            option: "facilities2",
            optValue: "facilities 2"
        }],
        "legal": [{
            option: "legal1",
            optValue: "Legal 1"
        }, {
            option: "legal2",
            optValue: "Legal 2"
        }]
    };

    var whatneeded = newValue;

    // Clear all of the choices from the What needed field choice list
    g_form.clearOptions('u_what_needed');

    g_form.addOption("u_what_needed", options[whatneeded][0].option, options[whatneeded][0].optValue);
    g_form.addOption("u_what_needed", options[whatneeded][1].option, options[whatneeded][1].optValue);
	g_form.addOption("u_what_needed", "other","Other");

    // If the form is loading and it is not a new record, set the u_what_needed value to the
    // value from the record before it was loaded
    if (isLoading && !g_form.isNewRecord()) {
        g_form.setValue('u_what_needed', whatneeded);
    }
}
